             UPDATE CNT_APRV_TEMPL SET REF_NO = 'TMPL'|| TO_CHAR(CREATED_ON,'YY') || '-'|| LPAD(TO_CHAR(NVL((SELECT NEXT_VAL FROM CNT_SEQ_DEF WHERE DOMAIN_ID =SELECT_RECORD.DOMAIN_ID AND SEQ_ID ='CBX_SEQ_APRV_TEMPL_REF_NO'),0)),6,'0')  
